import "./App.css";
import { useState } from "react";
import { Task } from "./Task";

function App() {
	const [todoLst, setTodoLst] = useState([]);
	const [newTask, setNewTask] = useState("");
	const [newDescr, setNewDescr] = useState("");

	const handleTask = (event) => {
		setNewTask(event.target.value);
	};

	const handleDescr = (event) => {
		setNewDescr(event.target.value);
	};

	const createTask = () => {
		const task = {
			taskID: todoLst.length === 0 ? 1 : todoLst[todoLst.length - 1].taskID + 1,
			taskName: newTask,
			taskDescr: newDescr,
			taskStatus: false,
		};
		setTodoList(task.taskName !== ""  ? [...todoList, task] : todoList);
	};

	const changeStatus = (id) => {
		setTodoLst(
			todoLst.map((task) => {
				if (task.id === id) {
					return { ...task, taskStatus: true };
				} else {
					return task;
				}
			})
		);
	};

	return (
		<div className="App">
			<div className="addTask">
				<div>
					<p>Title:</p>
					<input placeholder="Enter title" onChange={handleTask} />
				</div>
				<div>
					<p>Decription:</p>
					<input placeholder="Enter description" onChange={handleDescr} />
				</div>
				<button onClick={createTask}>Create</button>
			</div>
			<div>
				<div className="menuBar">
					<p>ID</p>
					<p>TITLE</p>
					<p>DESCRIPTION</p>
					<p>STATUS</p>
				</div>
				<div className="list">
					{todoLst.map((task) => {
						return (
							<Task
								taskID={task.id}
								taskName={task.taskName}
								taskDescr={task.taskDescr}
								taskStatus={task.taskStatus}
								changeStatus={changeStatus}
							/>
						);
					})}
				</div>
			</div>
		</div>
	);
}

export default App;
