import "./App.css";
import { useState } from "react";
import { Task } from "./Task";
import { Modal } from "./Modal";

function App() {
	const [todoLst, setTodoLst] = useState([]);
	const [taskName, setTaskName] = useState("");
	const [descr, setDescr] = useState("");
	const [isNameValid, setIsNameValid] = useState(true);

	const handleTaskName = (event) => {
		const value = event.target.value;
		setTaskName(value);
		setIsNameValid(value !== "");
	};

	const inputStyle = !isNameValid && { borderColor: "#FF000" };

	const handleDescr = (event) => {
		setDescr(event.target.value);
	};

	const createTask = () => {
		const task = {
			taskID: todoLst.length === 0 ? 1 : todoLst[todoLst.length - 1].taskID + 1,
			taskName: taskName,
			taskDescr: descr,
			taskStatus: false,
		};
		setTodoLst([...todoLst, task]);
	};

	const changeStatus = (id) => {
		setTodoLst(
			todoLst.map((task) => {
				if (task.taskID === id) {
					return { ...task, taskStatus: true };
				} else {
					return task;
				}
			})
		);
	};

	const showModal = (id) => {};

	return (
		<div className="App">
			<div className="addTask">
				<div>
					<p>Title:</p>
					<input
						// style={inputStyle}
						placeholder="Enter title"
						onChange={handleTaskName}
					/>
					<p className="errorText">This field is empty</p>
				</div>
				<div>
					<p>Decription:</p>
					<input placeholder="Enter description" onChange={handleDescr} />
				</div>
				<button type="submit" onClick={createTask}>
					Create
				</button>
			</div>
			<div>
				<div className="menuBar">
					<p>ID</p>
					<p>TITLE</p>
					<p>DESCRIPTION</p>
					<p>STATUS</p>
				</div>
				<div className="list">
					{todoLst.map((task) => {
						return (
							<Task
								onClick={showModal}
								taskID={task.taskID}
								taskName={task.taskName}
								taskDescr={task.taskDescr}
								taskStatus={task.taskStatus}
								changeStatus={changeStatus}
							/>
						);
					})}
				</div>
			</div>
		</div>
	);
}

export default App;
