import "./App.css";
import { useState } from "react";

function App() {
	const [todoLst, setTodoLst] = useState([]);
	const [newTask, setNewTask] = useState("");
	const [newDescr, setNewDescr] = useState("");

	const handleTask = (event) => {
		setNewTask(event.target.value);
	};

	const handleDescr = (event) => {
		setNewDescr(event.target.value);
	};

	const createTask = () => {

		setTodoLst([
			...todoLst,
			{
				taskID:
					todoLst.length === 0 ? 1 : todoLst[todoLst.length - 1].taskID + 1,
				taskName: newTask,
				taskDescr: newDescr,
				taskStatus: false,
			},
		]);
	};



	return (
		<div className="App">
			<div className="addTask">
				<div>
					<p>Title:</p>
					<input placeholder="Enter title" onChange={handleTask} />
				</div>
				<div>
					<p>Decription:</p>
					<input placeholder="Enter description" onChange={handleDescr} />
				</div>
				<button onClick={createTask}>Create</button>
			</div>
			<div>
				<div className="menuBar">
					<p>ID</p>
					<p>TITLE</p>
					<p>DESCRIPTION</p>
					<p>STATUS</p>
				</div>
				<div className="list">
					{todoLst.map((task, index) => {
						return (
							<p>
								{index}
								{task.taskName}
								{task.taskDescr}
							</p>
						);
					})}
				</div>
			</div>
		</div>
	);
}

export default App;
