import "./App.css";
import { useState } from "react";
import { Task } from "./Components/Task";
import { Modal } from "./Components/Modal";

function App() {
	const [todoLst, setTodoLst] = useState([]);
	const [taskName, setTaskName] = useState("");
	const [descr, setDescr] = useState("");
	const [error, setError] = useState(false);
	const [openModal, setOpenModal] = useState(false);
	const [selectIndex, setSelectIndex] = useState(0);

	// const handleClose = () => {
	// 	setOpenModal(false);
	// };

	const handleTaskName = (event) => {
		setTaskName(event.target.value);
	};

	const handleDescr = (event) => {
		setDescr(event.target.value);
	};

	const createTask = () => {
		if (taskName !== "") {
			const task = {
				taskID:
					todoLst.length === 0 ? 1 : todoLst[todoLst.length - 1].taskID + 1,
				taskName: taskName,
				taskDescr: descr,
				taskStatus: false,
			};
			setTodoLst([...todoLst, task]);
			setError(false);
		} else {
			setError(true);
		}
	};

	const changeStatus = (id) => {
		setTodoLst(
			todoLst.map((task) => {
				if (task.taskID === id) {
					return { ...task, taskStatus: true };
				} else {
					return task;
				}
			})
		);
	};

	return (
		<div className="App">
			<div className="addTask">
				<div>
					<p>Title:</p>
					<input
						style={{ borderColor: error && "red" }}
						placeholder="Enter title"
						onChange={handleTaskName}
					/>
					<p style={{ visibility: error && "visible" }} className="errorText">
						This field is empty
					</p>
				</div>
				<div>
					<p>Decription:</p>
					<input placeholder="Enter description" onChange={handleDescr} />
				</div>
				<button type="submit" onClick={createTask}>
					Create
				</button>
			</div>
			<div>
				<div className="menuBar">
					<p>ID</p>
					<p>TITLE</p>
					<p>DESCRIPTION</p>
					<p>STATUS</p>
				</div>
				<div className="list">
					{todoLst.map((task, index) => {
						return (
							<Task
								taskID={task.taskID}
								taskName={task.taskName}
								taskDescr={task.taskDescr}
								taskStatus={task.taskStatus}
								changeStatus={changeStatus}
								onClick={() => {
									setOpenModal(true);
									setSelectIndex(index);
								}}
							/>
						);
					})}

					{openModal && (
						<Modal
							taskName={todoLst[selectIndex].taskName}
							taskDescr={todoLst[selectIndex].taskDescr}
							taskStatus={todoLst[selectIndex].taskStatus}
							// onClose={handleClose}
						/>
					)}
				</div>
			</div>
		</div>
	);
}

export default App;
