export const Task = (props) => {
	return (
		<div className="task" onClick={props.onClick || showModal}>
			<p>{props.taskID} </p>
			<p>{props.taskName}</p>
			<p>{props.taskDescr}</p>
			<input
				type="checkbox"
				onChange={() => props.changeStatus(props.taskID)}
				checked={props.taskStatus ? true : false}
			/>
		</div>
	);
};
